                if (map is ImHashMap<K, V>.Entry l1)
                {
                    if (l1 is ImHashMapEntry<K, V> v0) yield return v0;
                    else foreach (var c in ((HashConflictingEntry<K, V>)l1).Conflicts) yield return c;
