            var key = newEntry.Key;
            if (oldEntry is ImHashMapEntry<K, V> kv)
                return kv.Key.Equals(key) ? new ImHashMapEntry<K, V>(newEntry.Hash, key, update(key, kv.Value, newEntry.Value))
